mapper.select:
SELECT * FROM (
    SELECT ROWNUM rn, t.*
    FROM (
        SELECT b.board_no, b.user_id, u.user_nickname, b.board_title, b.board_content,
               b.board_date, b.board_hit
        FROM post_board b
        LEFT JOIN opendata_users u ON b.user_id = u.user_id
        ORDER BY b.board_no DESC
    ) t
    WHERE ROWNUM <= #{offset} + #{size}
)
WHERE rn > #{offset}

mapper.update:
UPDATE post_board
SET 
board_title = #{boardTitle},
board_content = #{boardContent,jdbcType=CLOB}
WHERE board_no = #{boardNo}

UPDATE post_board
SET board_hit = board_hit + 1
WHERE board_no = #{boardNo}

